INSERT INTO data_source_products VALUES (true, 'db2', 'com.ibm.db2.jcc.DB2Driver', uuid_generate_v4(),'IBM DB2', null, 'db2');

INSERT
INTO data_source_product_icons
VALUES(
(SELECT data_source_product_id FROM data_source_products WHERE product_root='db2'),
 bytea E'\\'
);



INSERT INTO data_source_product_properties(data_source_product_property_id, data_source_product_id, name, type, order_index)
SELECT properties.id, prods.data_source_product_id, properties.name, properties.type, properties.ord
FROM data_source_products as prods
CROSS JOIN
(
  VALUES
  (uuid_generate_v4(), 'host'    , 'text'    , 1),
  (uuid_generate_v4(), 'port'    , 'text'    , 2),
  (uuid_generate_v4(), 'database', 'text'    , 3),
  (uuid_generate_v4(), 'user'    , 'text'    , 4),
  (uuid_generate_v4(), 'password', 'password', 5)
) as properties(id, name, type, ord)
WHERE prods.product_root='db2' ;


